if(financialOverview[0].statusText.trim()[0] === '-'){
    financialOverview[0].statusIcon = <ArrowDown className="h-4 w-4" aria-hidden="true" />;
    financialOverview[0].statusColorClass = "#CF4657";
}
export default function HomeFinancialOverview() {
    return(
        <Box
            component="section"
            aria-labelledby="financial-overview-heading"
            className="flex w-full flex-col items-start justify-between bg-[#F6F6F7B2] px-4 py-8 sm:px-6 md:px-10 lg:px-24 lg:py-12"
        >
		        <Box className ="w-fit">
		            <TitleWithGradient title="Financial Overview" id="financial-overview-heading" text_size="md" />
		        </Box>
                <Box
                    component="ul"
                    className="mt-6 grid w-full gap-4 sm:gap-5 md:gap-6 md:grid-cols-2 xl:grid-cols-4"
                >
                    {financialOverview.map((item) => (
                        <Box key={item.title} component="li" className="flex w-full p-4 bg-[#FFFFFF] rounded-xl">
                            <Box className="flex justify-center items-center h-[45px] w-[45px] rounded-lg" style={{backgroundColor: item.iconBg, color: item.accentColor}}>
                                {item.icon}
                            </Box>
                            <Box className="flex flex-col ml-2">
                                <Typography className="!font-[400] !text-[14px] ml-4 mt-1" style={{color: item.titleColor}}>
                                    {item.title}
                                </Typography>
                                <Typography className="!font-[500] !text-[18px] ml-4 mt-1 text-[#1C1A1A]">
                                    {item.amount}
                                </Typography>
                                <Box className="flex items-center  mt-1">
                                    <Box className={`flex items-center mr-2 ${item.statusColorClass}`}>
                                        {item.statusIcon}
                                        <Typography className="!font-[400] !text-[12px] !ml-1" style={{color: item.statusColorClass}}>
                                            {item.statusText}
                                        </Typography>
                                    </Box>
                                </Box>
                            </Box>
                        </Box>  
                    ))}
                </Box>
        </Box>
    );
}